a = prompt("How much do you like this teacher? Enter any number between 1 to 5 ( 5 = highest, 1 = lowest ), or 0 for no comment (TA/LF Section)", 5);
if (a < 1 || a > 5) throw new Error("Enter only 1-5 or 0! Please run script again!");
for (i = 0; i <= 99; i++) {
    k = Math.floor(10 * Math.random()) % 3 + (a - 1);
    if (k > 5) k = 5;
    if (k < 1) k = 1;
    if (a == 0) k = 0;
    [].slice.call(document.getElementsByName("score" + i)).forEach(function (e) {
        if (k == e.value) e.checked = true
    })
}
